代码示例




@implementation ViewController

- (void)viewDidAppear:(BOOL)animated {
    [super viewDidAppear:animated];

    // 跟踪进入视图事件
    [[DMHubSDK shared] trackOpenView:@"{viewName}"];
}

@end




